import React, { useEffect } from 'react'
import Question from '../../components/question/question'
import "./quiz.css"
import { useDispatch, useSelector } from "react-redux";
import { loadData } from '../../redux/actions';

const data = {
  "questions": [
    {
      "id": 1,
      "question": "Who composed Vande Matram ?",
      "options": [
        "Rabindra Nath Tagore",
        "Bankim Chandra chatterjee",
        "JL Nehru",
        "None of the above"
      ],
      "img": "data:image/jpeg;base64,"
    },
    {
      "id": 2,
      "question": "2, 5, 8, 11, 14, __ ?",
      "options": [
        "16",
        "19",
        "17",
        "18"
      ],
      "img": "data:image/jpeg;base64,"
    },
    {
      "id": 3,
      "question": "HTML is a ___ language",
      "options": [
        "procedural",
        "markup",
        "object oriented",
        "object based"
      ],
      "img": "data:image/jpeg;base64,"
    }
  ]
};

const Quiz = () => {
  console.log("-----Quiz-----");
  const state = useSelector((state) => state.quiz);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(loadData(data));
  }, [])

  return (
    state.questionList.length && <div className='quiz-container'>
      <h1>Quiz</h1>
      <div className="d-flex justify-content-end w-100 ">
        <div className="d-flex flex-wrap">
          <small className="me-4">Total time: 60min</small>
          <small>Time taken: 45min</small>
        </div>
      </div>
      <div className="question-container">
        <Question
          question={state.questionList[state.current_question_number - 1]}
          state={state} key={state.questionList[state.current_question_number - 1].id}
          startTime={Date.now()}
        />
      </div>
    </div>
  )
}

export default Quiz